import React, { useEffect } from "react";
import {
	ImageBackground,
	StyleSheet,
	Text,
	View,
	Image,
	Dimensions,
} from "react-native";
import BG from '../assets/images/download.jpg'
import Waves from '../assets/images/wave.svg'
import BG2 from '../assets/images/bg.png' 


const Splash =(props) => {
  
  useEffect(() => {
    setTimeout(() => {
      props.navigation.navigate("Login");
    }, 3000);
    
  
    
  }, [])
  

  
    return (
			<View style={styles.container}>
				<View resizeMode="cover" source={BG} style={styles.image}>
					<Image source={BG2} style={{ width: 300, height: 300, marginStart:20, }} />
					<Text style={styles.text}>Welcome To</Text>
					<Text style={styles.text}>Car Lift</Text>
				</View>
			</View>
		);
  }
  const android = Dimensions.get("window");
  const styles = StyleSheet.create({
		container: {
			flex: 1,
		},
		image: {
			flex: 1,
			backgroundColor: "#1F4690",
			justifyContent: "center",
		},
		text: {
			color: "white",
			fontSize: 42,
			lineHeight: 84,
			fontWeight: "bold",
			textAlign: "center",
			
		},
    bg:{
      width:android.width,
      
    }
	});


export default Splash;
