import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View, ImageBackground } from "react-native";
import BG from '../assets/images/download.jpg'

export default function SplashScreen() {
	const [splash, setsplash] = useState({ isVisible: true });
	const Hide_Splash_Screen = () => {
		setsplash({
			isVisible: false,
		});
	};
	useEffect(() => {
		const Timeout = () => {
			Hide_Splash_Screen();
		};
	}, [5000]);

	render();
	{
		let Splash_Screen = (
			<View style={styles.SplashScreen_RootView}>
				<View style={styles.SplashScreen_ChildView}>
					<Image
						source={{
							uri: "https://static.javatpoint.com/tutorial/react-native/images/react-native-tutorial.png",
						}}
						style={{ width: "100%", height: "100%", resizeMode: "contain" }}
					/>
				</View>
			</View>
		);
		return (
			<View style={styles.MainContainer}>
				<Text style={{ textAlign: "center" }}> Splash Screen Example</Text>
				{this.state.isVisible === true ? Splash_Screen : null}
			</View>
		);
	}
}
const styles = StyleSheet.create({
	MainContainer: {
		flex: 1,
		justifyContent: "center",
		alignItems: "center",
		paddingTop: Platform.OS === "ios" ? 20 : 0,
	},

	SplashScreen_RootView: {
		justifyContent: "center",
		flex: 1,
		margin: 10,
		position: "absolute",
		width: "100%",
		height: "100%",
	},

	SplashScreen_ChildView: {
		justifyContent: "center",
		alignItems: "center",
		backgroundColor: "#00BCD4",
		flex: 1,
	},
});
