import React, { useEffect } from "react";
import {
	
	StyleSheet,
	Text,
	View,
	Image,
	Dimensions,
} from "react-native";
import BG from '../assets/images/download.jpg'

import BG2 from '../assets/images/glow-modified.png' 
import { LinearGradient } from "expo-linear-gradient";


const Splash =(props) => {
  
  useEffect(() => {
    setTimeout(() => {
      props.navigation.navigate("Login")
	//   props.navigation.replace("Dashboard");
    }, 3000);
    
  
    
  }, [])
  

  
    return (
			<View style={styles.container}>
				<View
					
					
					style={styles.image}
					
				>
					<View style={{flexDirection:'column',alignItems:"center"}}>
						<Image source={BG2} style={{ width: 250, height: 250 }} />
						<Text style={styles.text}>Welcome To</Text>
						<Text style={styles.text}>Car Lift</Text>
					</View>
				</View>
			</View>
		);
  }
  const android = Dimensions.get("window");
  const styles = StyleSheet.create({
		container: {
			flex: 1,
			with: android.width * 1.2,
			height: android.height * 1.2,
			backgroundColor: "#0AA1DD",
		},
		image: {
			flex: 1,
			flexDirection: "column",
			justifyContent: "center",
			// backgroundColor: "#1F4690",
			justifyContent: "center",
		},
		text: {
			color: "white",
			fontSize: 42,

			fontWeight: "bold",
			textAlign: "center",
		},
		bg: {
			width: android.width,
		},
	});


export default Splash;
