import React from "react";
import {
	
	Text,
	View,
	
	StyleSheet,Dimensions,
    TouchableHighlight,KeyboardAvoidingView
} from "react-native";
import { TextInput } from "react-native-paper";

import Colors from "../assets/constants/Colors";


const HomeSearch = () => {
	

	return (
		<View style={styles.container}>
			<View style={styles.flexCenter}>
				<View style={styles.horizontalClip} />
			</View>
			

			<KeyboardAvoidingView>
				<Text style={{ fontSize: 18, fontWeight: "bold", marginTop: 20 }}>
					Enter Pickup Location
				</Text>
				<TextInput
					mode="outlined"
					label="Current location"
					
					style={styles.input}
				/>
				<Text style={{ fontSize: 18, fontWeight: "bold", marginTop: 10 }}>
					Enter Destination
				</Text>
				<TextInput
					mode="outlined"
					label="Drop location"
					
					style={styles.input}
				/>
				<TouchableHighlight
					
				>
					<View style={styles.button}>
						<Text style={{ color: "white", fontWeight: "bold" }}>
							
							Submit
						</Text>
					</View>
				</TouchableHighlight>
			</KeyboardAvoidingView>

			
		</View>
	);
};

export default HomeSearch;
const android = Dimensions.get("window");
const styles = StyleSheet.create({
	container: {
		flex: 1,
		paddingTop: 10,
		paddingBottom: 20,
	},
	input: {
		
		
		borderTopLeftRadius: 20,
		borderBottomLeftRadius: 20,
		borderBottomRightRadius: 20,
		width: android.width,
		backgroundColor:"white",
		height: 60,
		borderTopRightRadius: 20,
		shadowColor: "#000",
		// shadowOffset: {
		// 	width: 0,
		// 	height: 3,
		// },
		

		
	},
	button: {
		alignItems: "center",
		backgroundColor: "#1F4690",
		color: "white",
		padding: 15,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 3,
		},
		shadowOpacity: 0.29,
		shadowRadius: 4.65,

		elevation: 7,

		width: android.width * 0.94,
		borderTopLeftRadius: 20,
		borderBottomLeftRadius: 20,
		borderBottomRightRadius: 20,
		borderTopRightRadius: 20,

		marginTop: 20,
	},
	flexCenter: {
		alignItems: "center",
	},
	horizontalClip: {
		backgroundColor: Colors.mediumGrey,
		width: 60,
		height: 5,
		borderRadius: 25,
		marginBottom: 8,
	},
	inputBox: {
		backgroundColor: Colors.lightGrey,
		margin: 10,
		paddingVertical: 10,
		paddingHorizontal: 15,
		flexDirection: "row",
		justifyContent: "space-between",
		alignItems: "center",
		// borderRadius: 8
	},
	inputText: {
		borderRadius: 38,
		backgroundColor: "#e0e0e0",

		width: android.width * 0.94,
		height: 60,

		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 3,
		},

		shadowOpacity: 0.29,
		shadowRadius: 4.65,

		elevation: 7,
	},
	timeContainer: {
		flexDirection: "row",
		width: 100,
		justifyContent: "space-between",
		backgroundColor: Colors.background,
		padding: 8,
		alignItems: "center",
		borderRadius: 50,
	},
	row: {
		marginTop: 5,
		flexDirection: "row",
		alignItems: "center",
		padding: 20,
		borderBottomWidth: 1,
		borderBottomColor: Colors.mediumGrey,
	},
	iconContainer: {
		backgroundColor: "#b3b3b3",
		padding: 10,
		borderRadius: 25,
	},
	destinationText: {
		marginLeft: 15,
		fontWeight: "600",
		fontSize: 16,
		color: Colors.darkGrey,
	},
	displayRow: {
		flexDirection: "row",
		alignItems: "center",
	},
	lastRow: {
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "space-between",
		paddingHorizontal: 20,
		paddingVertical: 12,
	},
	pointsText: {
		marginLeft: 8,
		fontWeight: "600",
		fontSize: 14,
		color: Colors.darkGrey,
	},
	rewardsText: {
		fontWeight: "400",
		marginRight: 15,
		fontSize: 14,
		color: Colors.darkGrey,
	},
});
