import React, { useState,useRef } from "react";
import {
	StyleSheet,
	Text,
	View,
	Image,
	TouchableWithoutFeedback,
	StatusBar,
	SafeAreaView,
	Keyboard,
	TouchableOpacity,
	KeyboardAvoidingView,
	AppRegistry,
	Dimensions,
	Button,
	TouchableHighlight,
} from "react-native";
import { TextInput } from "react-native-paper";
import BG2 from "../assets/images/loginbg-removebg-preview.png"; 
import {firebaseconfig} from '../config'



const LoginScreen =(props)=> {
	const [number, onChangeNumber] = React.useState(null);
	const [phoneNumber, setphoneNumber] = useState('')
	 const recaptchaVerifier = useRef(null);
	 const [verificationId, setVerificationId] = useState(null);
	 const sendVerification = () => {
		const phoneProvider=new firebase.auth.PhoneAuthProvider();
		phoneProvider.verifyPhoneNumber(phoneNumber,recaptchaVerifier.current).then(setVerificationId);
		
			
				navigation.navigate("Verification");
		};
		const confirmCode = () => {
			
		};

	
		const { navigation } = props;

		return (
			<SafeAreaView style={styles.container}>
				<StatusBar barStyle="light-content" />
				<KeyboardAvoidingView behavior="padding">
					<TouchableWithoutFeedback onPress={Keyboard.dismiss}>
						<View>
							<View style={styles.topText}>
								<Text style={{ color: "white" }}>Logo Here</Text>
								<Text
									style={{
										fontSize: 32,
										marginStart: 50,
										color: "white",
										fontWeight: "bold",
									}}
								>
									Welcome To
								</Text>
							</View>
							<Text
								style={{
									textAlign: "center",
									color: "white",
									fontSize: 32,
									fontWeight: "bold",
								}}
							>
								Lift Lae Ly
							</Text>

							<Image source={BG2} style={{ marginTop: 40 }} />

							<View>
								<View>
									<Text
										style={{ color: "white", fontSize: 18, fontWeight: "bold" }}
									>
										Phone Number
									</Text>
									<TextInput
										label=" Enter Your Phone Number"
										keyboardType="numeric"
										style={styles.input}
										onChangeText={onChangeNumber}
										value={number}
									/>
								</View>
								<TouchableHighlight
									onPress={sendVerification}
									
									
								>
									<View style={styles.button}>
										<Text style={{color:"white"}}>Get  OTP</Text>
									</View>
								</TouchableHighlight>
							</View>
						</View>
					</TouchableWithoutFeedback>
				</KeyboardAvoidingView>
			</SafeAreaView>
		);
	}

const android = Dimensions.get("window");
const styles = StyleSheet.create({
	container: {
		flex: 1,
		width: android.width ,
		height: android.height ,
		backgroundColor: "#1F4690",
	},

	topText: {
		flexDirection: "row",
		// alignItems:'center'
		// justifyContent: "space-around",
		marginTop: 30,
	},
	button: {
		alignItems: "center",
		backgroundColor: "#FFD24C",
		color: "white",
		padding: 15,
		borderTopLeftRadius: 20,
		borderBottomLeftRadius: 20,
		borderBottomRightRadius: 20,
		borderTopRightRadius: 20,

		width: android.width,
		marginTop: 40,
	},
	input: {
		marginTop: 15,
		borderTopLeftRadius: 20,
		borderBottomLeftRadius: 20,
		borderBottomRightRadius: 20,
		width: android.width,
		borderTopRightRadius: 20,
	},
});

export default LoginScreen;
