import React, { useRef } from "react";
import {
	StyleSheet,
	Text,
	View,
	Image,
	TouchableWithoutFeedback,
	StatusBar,
	SafeAreaView,
	Keyboard,
	TouchableOpacity,
	KeyboardAvoidingView,
	Dimensions,
	TouchableHighlight,
} from "react-native";
import "@firebase/auth";
import { TextInput } from "react-native-paper";
import BG2 from "../assets/images/login_bg-removebg-preview.png";
import { FirebaseRecaptchaVerifierModal } from "expo-firebase-recaptcha";
import firebase from "firebase/compat/app";
import "firebase/compat/auth";
import "firebase/compat/firestore";
import PhoneInput from "react-native-phone-number-input";
import { LinearGradient } from "expo-linear-gradient";
import { BlurView } from "expo-blur";
import Logo from '../assets/images/glow-modified.png'
import Verification from "./Verification";

// Initialize Firebase JS SDK
// https://firebase.google.com/docs/web/setup
try {
	firebase.initializeApp({
		apiKey: "AIzaSyBlyd5zF3D9D5MV9Pkn0tqkvG5hCmJ3R6c",
		authDomain: "lift-lae-ly.firebaseapp.com",
		projectId: "lift-lae-ly",
		storageBucket: "lift-lae-ly.appspot.com",
		messagingSenderId: "739366618024",
		appId: "1:739366618024:web:c0aa0588e8c3921722139d",
		measurementId: "G-T5C7S28NJ9",
	});
} catch (err) {
	// ignore app already initialized error in snack
}
 navigationOptions = ({ navigation }) => SetNavOptions('Topics', navigation)

const LoginScreen = (props) => {
	const { navigation } = props;
	const recaptchaVerifier = React.useRef('');
	const phoneInput = useRef(null);
	const [phoneNumber, setPhoneNumber] = React.useState();
	const [verificationId, setVerificationId] = React.useState(null);
	const [verificationCode, setVerificationCode] = React.useState();
	
	const firebaseConfig = firebase.apps.length
		? firebase.app().options
		: undefined;
	const [message, showMessage] = React.useState(
		!firebaseConfig || Platform.OS === "web"
			? {
					text: "To get started, provide a valid firebase config in App.js and open this snack on an iOS or Android device.",
			  }
			: undefined
	);

	return (
		<LinearGradient
			style={styles.container}
			colors={["#1F4690", "#646FD4"]}
			start={{
				x: 1,
				y: 1,
			}}
			end={{
				x: 0,
				y: 0,
			}}
		>
			<FirebaseRecaptchaVerifierModal
				ref={recaptchaVerifier}
				firebaseConfig={firebaseConfig}
			/>
			<StatusBar barStyle="light-content" />
			<KeyboardAvoidingView behavior="padding">
				<TouchableWithoutFeedback onPress={Keyboard.dismiss}>
					<View>
						<View style={styles.topText}>
							{/* <Text style={{ color: "white" }}>Logo Here</Text> */}
							<Image
								source={Logo}
								style={{ width: 80, height: 80, marginStart: 10 }}
							/>
							<View
								style={{
									flexDirection: "column",
									alignItems: "center",
									marginStart: 40,
								}}
							>
								<Text
									style={{
										fontSize: 42,
										marginTop:10,

										color: "white",
										fontWeight: "bold",
									}}
								>
									Welcome To
								</Text>
								<Text
									style={{
										textAlign: "center",
										color: "white",
										fontSize: 42,
										fontWeight: "bold",
									}}
								>
									Lift Lae Ly
								</Text>
							</View>
						</View>

						<Image source={BG2} />

						<BlurView
							intensity={75}
							style={{
								height: 700,
								borderTopStartRadius: 15,
								borderTopEndRadius: 15,
								paddingLeft: 10,
								paddingRight: 10,
							}}
						>
							<View>
								<Text
									style={{
										color: "white",
										fontSize: 18,
										marginStart: 10,
										fontWeight: "bold",
										marginTop: 20,
									}}
								>
									Phone Number
								</Text>
								<PhoneInput
									ref={phoneInput}
									label=" Enter Your Phone Number"
									containerStyle={styles.input}
									defaultValue={phoneNumber}
									textContainerStyle={styles.textInput}
									placeholder="+923123456789"
									autoFocus
									layout="first"
									defaultCode="PK"
									keyboardType="phone-pad"
									onChangeText={(phoneNumber) => setPhoneNumber(phoneNumber)}
									onChangeFormattedText={(text) => {
										setPhoneNumber(text);
									}}
								/>
							</View>
							<TouchableHighlight
								onPress={async () => {
									// The FirebaseRecaptchaVerifierModal ref implements the
									// FirebaseAuthApplicationVerifier interface and can be
									// passed directly to `verifyPhoneNumber`.
									try {
										const phoneProvider = new firebase.auth.PhoneAuthProvider();

										const verificationId =
											await phoneProvider.verifyPhoneNumber(
												phoneNumber,
												recaptchaVerifier.current
											);
										setVerificationId(verificationId);
										navigation.replace("Verification",{verificationId})
										
									} catch (err) {
										showMessage({
											text: `Error: ${err.message}`,
											color: "red",
										});
									}
								}}
							>
								<View style={styles.button}>
									<Text style={{ color: "white", fontWeight: "bold" }}>
										Get OTP
									</Text>
								</View>
							</TouchableHighlight>
							{message ? (
								<TouchableOpacity
									style={[
										StyleSheet.absoluteFill,
										{ backgroundColor: 0xffffffee, justifyContent: "center" },
									]}
									onPress={() => showMessage(undefined)}
								>
									<Text
										style={{
											color: message.color || "blue",
											fontSize: 17,
											textAlign: "center",
											margin: 20,
										}}
									>
										{message.text}
									</Text>
								</TouchableOpacity>
							) : undefined}
						</BlurView>
					</View>
				</TouchableWithoutFeedback>
			</KeyboardAvoidingView>
		</LinearGradient>
	);
};

const android = Dimensions.get("window");
const styles = StyleSheet.create({
	container: {
		flex: 1,
		width: android.width,
		height: android.height * 1.5,
	},

	topText: {
		flexDirection: "row",
		// alignItems:'center'
		// justifyContent: "space-around",
		marginTop: 60,
	},
	button: {
		alignItems: "center",
		backgroundColor: "#1F4690",
		color: "white",
		padding: 15,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 3,
		},
		shadowOpacity: 0.29,
		shadowRadius: 4.65,

		elevation: 7,

		width: android.width * 0.94,
		borderTopLeftRadius: 20,
		borderBottomLeftRadius: 20,
		borderBottomRightRadius: 20,
		borderTopRightRadius: 20,

		marginTop: 60,
	},
	input: {
		marginTop: 10,
		borderTopLeftRadius: 20,
		borderBottomLeftRadius: 20,
		borderBottomRightRadius: 20,
		width: android.width * 0.94,
		height: 60,
		borderTopRightRadius: 20,
		shadowColor: "#000",
		shadowOffset: {
			width: 0,
			height: 3,
		},
		shadowOpacity: 0.29,
		shadowRadius: 4.65,

		elevation: 7,
	},
	textInput: {
		paddingVertical: 0,
		borderBottomRightRadius: 20,
		borderTopRightRadius: 20,
	},
});

export default LoginScreen;
